body.script:
// Immediate alert to confirm JS runs
(function() {
    // Create debug console immediately
    var debug = document.createElement('div');
    debug.id = 'debug-log';
    debug.style.cssText = 'position:fixed;top:60px;right:0;width:350px;height:400px;background:#000;color:#0f0;padding:10px;font-size:12px;font-family:monospace;z-index:9999;overflow-y:auto;border:3px solid yellow;';

    var header = document.createElement('div');
    header.style.color = 'yellow';
    header.style.fontWeight = 'bold';
    header.style.marginBottom = '5px';
    header.textContent = 'DEBUG CONSOLE';
    debug.appendChild(header);

    // Wait for body or insert into html element
    var insertDebug = function() {
        if (document.body) {
            document.body.appendChild(debug);
        } else if (document.documentElement) {
            document.documentElement.appendChild(debug);
        }
    };

    window.debugLog = function(msg, color) {
        var line = document.createElement('div');
        if (color) line.style.color = color;
        line.textContent = '[' + new Date().toLocaleTimeString() + '] ' + msg;
        debug.appendChild(line);
        debug.scrollTop = debug.scrollHeight;
    };

    // Try to insert immediately
    insertDebug();

    // Also try on DOMContentLoaded
    if (document.addEventListener) {
        document.addEventListener('DOMContentLoaded', insertDebug);
    }

    debugLog('=== SCRIPT STARTED ===', 'cyan');
    debugLog('Time: ' + new Date().toLocaleString());
    debugLog('URL: ' + window.location.href);
    debugLog('Ready state: ' + document.readyState);
    debugLog('Body exists: ' + !!document.body, document.body ? 'lime' : 'red');
    debugLog('DocumentElement: ' + !!document.documentElement);

    // Set current year when ready
    var setYear = function() {
        try {
            var yearEl = document.getElementById('year');
            debugLog('Year element: ' + (yearEl ? 'FOUND' : 'NOT FOUND'), yearEl ? 'lime' : 'orange');
            if (yearEl) {
                yearEl.textContent = new Date().getFullYear();
                debugLog('Year set to: ' + new Date().getFullYear(), 'lime');
            }
        } catch(e) {
            debugLog('ERROR setting year: ' + e.message, 'red');
        }

        // Count elements
        var posts = document.querySelectorAll('.card, .post-card, article');
        var widgets = document.querySelectorAll('.widget');
        var sections = document.querySelectorAll('b\\:section, [class*="section"]');

        debugLog('Posts found: ' + posts.length, posts.length > 0 ? 'lime' : 'orange');
        debugLog('Widgets found: ' + widgets.length, widgets.length > 0 ? 'lime' : 'orange');
        debugLog('Sections found: ' + sections.length, sections.length > 0 ? 'lime' : 'orange');
        debugLog('=== SCAN COMPLETE ===', 'cyan');
    };

    // Run on load
    if (document.readyState === 'complete' || document.readyState === 'interactive') {
        setTimeout(setYear, 100);
    } else {
        if (window.addEventListener) {
            window.addEventListener('load', setYear);
            document.addEventListener('DOMContentLoaded', setYear);
        }
    }
})();
