body.script:
(function() {
    'use strict';

    const ready = (callback) => {
        if (document.readyState !== 'loading') {
            callback();
        } else {
            document.addEventListener('DOMContentLoaded', callback);
        }
    };

    const initMobileNav = () => {
        const burgerBtn = document.getElementById('vt-burger-btn');
        const mobileMenu = document.getElementById('vt-mobile-menu');

        if (!burgerBtn || !mobileMenu) return;

        burgerBtn.addEventListener('click', function() {
            mobileMenu.classList.toggle('vt-menu-active');
            const icon = burgerBtn.querySelector('i');
            if (mobileMenu.classList.contains('vt-menu-active')) {
                icon.classList.remove('fa-bars');
                icon.classList.add('fa-times');
            } else {
                icon.classList.remove('fa-times');
                icon.classList.add('fa-bars');
            }
        });
    };

    const initSmoothScroll = () => {
        const links = document.querySelectorAll('a[href^="#"]');

        links.forEach(link => {
            link.addEventListener('click', function(e) {
                const href = this.getAttribute('href');

                if (href === '#') {
                    e.preventDefault();
                    return;
                }

                const target = document.querySelector(href);

                if (target) {
                    e.preventDefault();
                    target.scrollIntoView({
                        behavior: 'smooth',
                        block: 'start'
                    });
                }
            });
        });
    };

    const initExternalLinks = () => {
        const links = document.querySelectorAll('a[href^="http"]');

        links.forEach(link => {
            const href = link.getAttribute('href');
            const currentDomain = window.location.hostname;

            if (!href.includes(currentDomain)) {
                link.setAttribute('target', '_blank');
                link.setAttribute('rel', 'noopener noreferrer');
            }
        });
    };

    const initBackToTop = () => {
        let backToTopBtn = document.getElementById('vt-back-to-top');

        if (!backToTopBtn) {
            backToTopBtn = document.createElement('button');
            backToTopBtn.id = 'vt-back-to-top';
            backToTopBtn.innerHTML = '↑';
            backToTopBtn.setAttribute('aria-label', 'Back to top');
            document.body.appendChild(backToTopBtn);
        }

        window.addEventListener('scroll', () => {
            if (window.pageYOffset > 300) {
                backToTopBtn.classList.add('show');
            } else {
                backToTopBtn.classList.remove('show');
            }
        });

        backToTopBtn.addEventListener('click', () => {
            window.scrollTo({
                top: 0,
                behavior: 'smooth'
            });
        });
    };

    ready(() => {
        initMobileNav();
        initSmoothScroll();
        initExternalLinks();
        initBackToTop();
        console.log('Blogger Theme initialized successfully!');
    });

})();
