body.script:
// Debug logging to page
window.debugLog = function(msg) {
    var debug = document.getElementById('debug-log');
    if (!debug) {
        debug = document.createElement('div');
        debug.id = 'debug-log';
        debug.style.cssText = 'position:fixed;top:0;right:0;width:300px;height:500px;background:#000;color:#0f0;padding:10px;font-size:11px;font-family:monospace;z-index:9999;overflow-y:auto;border:2px solid #f43f5e;';
        document.body.appendChild(debug);
    }
    var line = document.createElement('div');
    line.textContent = '[' + new Date().toLocaleTimeString() + '] ' + msg;
    debug.appendChild(line);
    debug.scrollTop = debug.scrollHeight;
};

debugLog('Script loaded!');
debugLog('Document ready state: ' + document.readyState);
debugLog('Body: ' + (document.body ? 'exists' : 'missing'));
debugLog('Window size: ' + window.innerWidth + 'x' + window.innerHeight);

// Set current year
try {
    var yearEl = document.getElementById('year');
    debugLog('Year element: ' + (yearEl ? 'found' : 'not found'));
    if (yearEl) {
        yearEl.textContent = new Date().getFullYear();
        debugLog('Year set to ' + new Date().getFullYear());
    }
} catch(e) {
    debugLog('Error setting year: ' + e.message);
}

// Check for content
var posts = document.querySelectorAll('.card, [data-post]');
debugLog('Posts found: ' + posts.length);

var widgets = document.querySelectorAll('.widget');
debugLog('Widgets found: ' + widgets.length);

debugLog('Ready!');
